passages.text:
a {text-decoration:none !important}
body { color:rgb(255,255,255) !important }
body { text-align:left; width:70% !important }
body { font-size:75% !important }

.blur{
/* sets transition */
transition: all 0.25s ease-in-out;
-moz-transition: all 0.25s ease-in-out;
-webkit-transition: all 0.25s ease-in-out;
}

.blur:hover{
color:transparent;
/*adjusts blur amount with text shadow */
text-shadow: #555 0 0 5px;
}

.blur::-moz-selection{
background-color:Transparent;
color:transparent;
}

.blur::selection {
background-color:Transparent;
color:transparent;
}

.unblur::-moz-selection{
background-color:Transparent;
color:transparent;
}

.unblur::selection {
background-color:Transparent;
color:transparent;
}

.unblur{
color:transparent;
/*adjusts blur amount with text shadow */
text-shadow: #555 0 0 5px;
}

.unblur:hover{
/* sets transition */
transition: all 0.25s ease-in-out;
-moz-transition: all 0.25s ease-in-out;
-webkit-transition: all 0.25s ease-in-out;
}

.dark{
color:rgb(10,10,10);
}


#sidebar {visibility:hidden!important;}

#footer{display:none!important;}

#toolbar{display:none!important;}

