passages.text:
h1 {
	text-align:center;
	line-height:120%;
	font-size:24pt;
	font-weight:bold;
}

h2 {
	font-size:18pt;
	line-height:120%;
	text-align:center;
	font-weight:normal;
	margin-top:-15px;
}

h3 {
	font-size:16pt;
	text-align:center;
	margin-top:30px;
}

body {
	background-color: #e9d7a8;
	margin:15px !important;
	color: black;
}

.passage {
	font-size: 10pt;
	min-width:200px;
	max-width:600px;
	padding:15px !important;
	margin:0 auto !important;
	background-color: #f7f0df;
	border:solid 3px #5c84b8;
	opacity:1 !important;
}

.passage li {
	margin:0px !important;
	margin:0 auto !important;
}

#passages
{
	border-left:0px !important;
	margin:0 auto !important;
	padding:0px !important;
}

#sidebar {
	display: none !important;
}

a.internalLink,a.externalLink,a.back {
	color:#365680;
	font-weight:bold;
	text-decoration:none;
}

a.internalLink:hover,a.back:hover {
	color:#365680;
	font-weight:bold;
	text-decoration:underline;
}

a.externalLink {
	color:#365680;
	font-weight:normal;
	text-decoration:none;
}

a.externalLink:hover {
	color:#365680;
	font-weight:normal;
	text-decoration:underline;
}

::selection
{
color:white;
background-color:#5c84b8;
}
::-moz-selection
{
color:white;
background-color:#f7f0df;
}
