macros['notEnoughData'] = 
{
	handler: function(place, object, parameters, parser)
	{
	  if(parameters.length > 2){
		var name = parameters[0];
		var number = parameters[1];
		var content = parameters[2];

		var data = state.history[0].variables["player"]["data"];

	    var index = arrayObjectIndexOf(data, name, "Name");
		//Check if the player has the right amount of data, and display the content if so.
		if (index > -1) {
			if(data[index].Number < number) new Wikifier(place, content);
		}
		//Player has no data. Check if we display under this circumstance.
		else{
			if(0 < number) new Wikifier(place, content);
		}

	  }

	}
}


function arrayObjectIndexOf(myArray, searchTerm, property) {
    for(var i = 0, len = myArray.length; i < len; i++) {
        if (myArray[i][property] === searchTerm) return i;
    }
    return -1;
}

macros['populateMapVariables'] = {
	handler: function(place, object, parameters, parser)
	{

if(state.history[0].variables["doneFirstMapEver"] != 1){

	//Generate a random seed. Will be used later with generic locations
	//to ensure uniqueness.
	var randomSeed = Math.floor((Math.random() * 1000) + 1);
	state.history[0].variables["randomSeed"] = randomSeed;

	//Get the world map height and width
	var wmap_height = state.history[0].variables["map_n_y"];
	var wmap_width = state.history[0].variables["map_n_x"];

	//Now make the world map.
	var worldMapArray = new Array(wmap_height);
	var worldDestsArray = new Array(wmap_height);
	var worldColourArray = new Array(wmap_height);

	for(var i = 0; i < wmap_height; i++){
		worldMapArray[i] = new Array(wmap_width);
		worldDestsArray[i] = new Array(wmap_width);
		worldColourArray[i] = new Array(wmap_width);
			for(var j = 0; j < wmap_width; j++){
			var charMap = generateMapChars(12, 8);
			charMap = applyGlass(12, 8, charMap);
			charMap = applyEdges(12, 8, charMap, wmap_height, i);
			//Fill world with generated map data
				worldMapArray[i][j] = charMap;
				worldDestsArray[i][j] = generateMapDestinations(12, 8, charMap);
				worldColourArray[i][j] = generateMapColours(12, 8, charMap);
			}
	}

	//Setup initial location
	var x_loc = Math.floor((Math.random() * 10))+1;
	var y_loc = Math.floor((Math.random() * 6))+1;

	state.history[0].variables["location_x"] = x_loc;
	state.history[0].variables["location_y"] = y_loc;

	//And Allocate the special 'Your Habitat' destination on the initial location.
	worldMapArray[1][1][y_loc][x_loc] = "⌂";
	worldDestsArray[1][1][y_loc][x_loc] = "Your Habitat";
	worldColourArray[1][1][y_loc][x_loc] = "#FFFF00";

	state.history[0].variables["worldMap"] = worldMapArray;
	state.history[0].variables["worldDests"] = worldDestsArray;
	state.history[0].variables["worldColours"] = worldColourArray;





	state.history[0].variables["doneFirstMapEver"] = 1;

}

//Get the current world map location...
var wmap_x = state.history[0].variables["map_x"];
var wmap_y = state.history[0].variables["map_y"];
var worldMap = state.history[0].variables["worldMap"]
var worldDests = state.history[0].variables["worldDests"];
var worldColours = state.history[0].variables["worldColours"];

//Now choose the map from the world map array.
state.history[0].variables["mapChars"] = worldMap[wmap_y][wmap_x];
state.history[0].variables["mapDestinations"] = worldDests[wmap_y][wmap_x];
state.history[0].variables["mapColours"] = worldColours[wmap_y][wmap_x];


	}
}

generateMapColours = function(width, height, charMap){
var mapArray = new Array(height);
//Go through the char map and assign appropriate destinations.
for(var i = 0; i < height; i++){
mapArray[i] = new Array(width);
	for(var j = 0; j < width; j++){	
		var colour = "#FFF";
		//Generic
		if(charMap[i][j] == "≈"){
			var num = Math.floor((Math.random() * 6) + 1);
			if(num == 1) colour = "#EEFFFF";
			else if(num == 2) colour = "#EAFFFF";
			else if(num == 3) colour = "#E5FFFF";
			else if(num == 4) colour = "#DEFFFF";
			else if(num == 5) colour = "#D6FFFF";
			else if(num == 6) colour = "#CCFFFF";
		}
		else if(charMap[i][j] == "▲"){
			var num = Math.floor((Math.random() * 6) + 1);
			if(num == 1) colour = "#FFF";
			else if(num == 2) colour = "#EEE";
			else if(num == 3) colour = "#DDD";
			else if(num == 4) colour = "#CCC";
			else if(num == 5) colour = "#BBB";
			else if(num == 6) colour = "#AAA";
		}
		else if(charMap[i][j] == "♠"){
			var num = Math.floor((Math.random() * 6) + 1);
			if(num == 1) colour = "#D6FFD6";
			else if(num == 2) colour = "#DEFFDE";
			else if(num == 3) colour = "#EEFFFF";
			else if(num == 4) colour = "#EAFFFF";
			else if(num == 5) colour = "#B2CCB2";
			else if(num == 6) colour = "#C8E0E0";
		}
		else if(charMap[i][j] == "♣"){
			var num = Math.floor((Math.random() * 6) + 1);
			if(num == 1) colour = "#D6FFD6";
			else if(num == 2) colour = "#DEFFDE";
			else if(num == 3) colour = "#EEFFFF";
			else if(num == 4) colour = "#EAFFFF";
			else if(num == 5) colour = "#B2CCB2";
			else if(num == 6) colour = "#C8E0E0";
		}
		else if(charMap[i][j] == "⌂") colour = "#666";
		else if(charMap[i][j] == "=") colour = "#666";
		else if(charMap[i][j] == "░") colour = "#FFF";
		//TODO: Other colours
		mapArray[i][j] = colour;
	}
}
return mapArray;
}



applyEdges = function(width, height, charMap, wmap_height, current_height){

//Go through the char map and assign '=' to the top or bottom
//edge of the map

var i = 0;

if(current_height == wmap_height-1) i = height-1;
else if(current_height != 0) return charMap;


	for(var j = 0; j < width; j++){	
		charMap[i][j] = "=";
	}

return charMap;
}

applyGlass = function(width, height, charMap){

//Go through the char map and assign # to the left/right sides
//of each map. Except where the maps are top or bottom

	for(var i = 0; i < height; i++){
		charMap[i][0] = "░";
		charMap[i][width-1] = "░";
	}

return charMap;
}


generateMapDestinations = function(width, height, charMap){
var mapArray = new Array(height);
//Go through the char map and assign appropriate destinations.
for(var i = 0; i < height; i++){
mapArray[i] = new Array(width);
	for(var j = 0; j < width; j++){	
		var destination = "u";
		//Generic
		if(charMap[i][j] == "≈") destination = "Ice";
		else if(charMap[i][j] == "▲") destination = "Mountains";
		else if(charMap[i][j] == "♠") destination = "Snowforest";
		else if(charMap[i][j] == "♣") destination = "Snowforest";
		else if(charMap[i][j] == "⌂") destination = "Habitat";
		else if(charMap[i][j] == "=") destination = "The Edge";
		else if(charMap[i][j] == "░") destination = "Plains of Glass";
		//TODO: Special and specific destinations.
		//Alternatively, they could come to exist during Gameplay.
		//Via exploration and discovery.
		//We'll see!!!
		mapArray[i][j] = destination;
	}
}
return mapArray;
}


generateMapChars = function(width, height){

var mapArray = new Array(height);

//Generate a map.
for(var i = 0; i < height; i++){
mapArray[i] = new Array(width);
	for(var j = 0; j < width; j++){
		mapArray[i][j] = getRandomMapChar();
	}
}

//do smoothing/sanity modifications here
mapArray = smoothMap(width, height, mapArray);
mapArray = smoothMap(width, height, mapArray);
mapArray = smoothMap(width, height, mapArray);



return mapArray;

}

getRandomMapChar = function(){

var num = Math.floor((Math.random() * 100) + 1); 

if (num > 99) return "⌂";

if(num < 20) return "▲";

if(num < 40){
	if(Math.floor((Math.random() * 10) + 1) < 6) return "♣";
	else return "♠";
}

return "≈";

}


smoothMap = function(width, height, map){

var mapArray = map;

//Iterate through the map characters and modify
//appropriately

for(var i = 0; i < height; i++){
	for(var j = 0; j < width; j++){
	//First get the character
	var character = mapArray[i][j]; 

	//Now get the surrounding characters
	var cN = "";
	var cS = "";
	var cE = "";
	var cW = "";
	var cNE = "";
	var cNW = "";
	var cSE = "";
	var cSW = "";

//Height is min and Width is min
if(i == 0 && j == 0){
	cS = mapArray[i+1][j]; 
	cE = mapArray[i][j+1]; 
	cSE = mapArray[i+1][j+1]; 
}
//Height is min and Width is max
else if(i == 0 && j == width-1){
	cS = mapArray[i+1][j]; 
	cW = mapArray[i][j-1]; 
	cSW = mapArray[i+1][j-1]; 
}
//Height is max and Width is min
else if(i == height-1 && j == 0){
	cN = mapArray[i-1][j]; 
	cE = mapArray[i][j+1]; 
	cNE = mapArray[i-1][j+1]; 
}
//Height is max and Width is max
else if(i == height-1 && j == width-1){
	cN = mapArray[i-1][j]; 
	cW = mapArray[i][j-1]; 
	cNW = mapArray[i-1][j-1]; 
}
//Height is min
else if(i == 0){
	cW = mapArray[i][j-1]; 
	cE = mapArray[i][j+1]; 
	cS = mapArray[i+1][j]; 
	cSW = mapArray[i+1][j-1]; 
	cSE = mapArray[i+1][j+1]; 
}
//Height is max
else if(i == height-1){
	cW = mapArray[i][j-1]; 
	cE = mapArray[i][j+1]; 
	cN = mapArray[i-1][j]; 
	cNE = mapArray[i-1][j+1]; 
	cNW = mapArray[i-1][j-1]; 
}
//Width is min
else if(j == 0){
	cE = mapArray[i][j+1]; 
	cN = mapArray[i-1][j]; 
	cNE = mapArray[i-1][j+1]; 
	cS = mapArray[i+1][j]; 
	cSE = mapArray[i+1][j+1]; 
}
//Width is max
else if(j == width-1){
	cN = mapArray[i-1][j]; 
	cS = mapArray[i+1][j]; 
	cW = mapArray[i][j-1]; 
	cNW = mapArray[i-1][j-1]; 
	cSW = mapArray[i+1][j-1]; 
}


if(character == "▲"){
var chance = 9;
	if(cN == "▲" || cN == "") chance = chance-1;
	if(cS == "▲" || cS == "") chance = chance-1;
	if(cW == "▲" || cW == "") chance = chance-1;
	if(cE == "▲" || cE == "") chance = chance-1;
	if(cNW == "▲" || cNW == "") chance = chance-1;
	if(cNE == "▲" || cNE == "") chance = chance-1;
	if(cSE == "▲" || cSE == "") chance = chance-1;
	if(cSW == "▲" || cSW == "") chance = chance-1;
	if(Math.floor((Math.random() * 10) + 1) < chance){
		mapArray[i][j] = "≈";
	}
}
else if(character == "♠" || character == "♣"){
var chance = 9;
	if(cN == "♠" || cN == "♣" || cN == "") chance = chance-1;
	if(cS == "♠" || cS == "♣" || cS == "") chance = chance-1;
	if(cW == "♠" || cW == "♣" || cW == "") chance = chance-1;
	if(cE == "♠" || cE == "♣" || cE == "") chance = chance-1;
	if(cNW == "♠" || cNW == "♣" || cNW == "") chance = chance-1;
	if(cNE == "♠" || cNE == "♣" || cNE == "") chance = chance-1;
	if(cSE == "♠" || cSE == "♣" || cSE == "") chance = chance-1;
	if(cSW == "♠" || cSW == "♣" || cSW == "") chance = chance-1;
	if(Math.floor((Math.random() * 10) + 1) < chance){
		mapArray[i][j] = "≈";
	}
}


	}
}

return mapArray;

}

(function(){
postrender.typewriter = function (b) {
	if(this.tags){
		var r=new RegExp("t8n.typewriter.([0-9]+)(?:[^0-9]|$)","g");
		var t=r.exec(this.tags.toString());

		if(t){typeout(b,t[1]+0);}
	}
return b;
};

var typeout=function(c,t){
	var Furl=function(current){
		this.n=current;
		this.out=false;
		this.data=current.nodeValue;
		current.nodeValue="";
		this.kids=[];
		var cn=current.childNodes;

		if(current.style && current.style.display=="none"){return;}
			while(cn.length>0){
				var f=new Furl(cn[0]);
				current.removeChild(cn[0]);
				f.out=true;
				this.kids.push(f);
			}
};

var nodes=new Furl(c);

var unfurl=function(furled,d){
	var n=furled.n;
	if(furled.out){
		d.appendChild(n);
		furled.out=false;
	}

	if(furled.data){
		n.nodeValue+=furled.data[0];
		furled.data=furled.data.slice(1);
		return true;
	}

	for(var j=0;j<furled.kids.length;j++){
		var ret=unfurl(furled.kids[j],n);
		if(ret){return true;}
	}

	return false;
};

var title=state.history[0].passage.title;
var intr=setInterval(function(){if(state.history[0].passage.title==title&&unfurl(nodes,null)){return;}
clearInterval(intr);},t);};}());

