getCurrentDataVisual = function(n){
	var num = n/2;
	if(num > 50) num = 50;
	var thing = "";
	var i;
	for(i = 0; i<num;i++) thing = thing+"█"
	if(num%2)for(j = num; j<49;j++) thing = thing+"&nbsp;"
	else for(j = num; j<50;j++) thing = thing+"&nbsp;"
	return thing;
}

