getMapChar = function(chars, dests, cols, vx, vy){

	var char = chars[vy][vx]; 
	var destination = dests[vy][vx];
	var color = cols[vy][vx];

	//Check if it's the current region. 
   var loc_y = state.history[0].variables["location_y"];
   var loc_x = state.history[0].variables["location_x"];

if(vx === loc_x && vy === loc_y){
return "<span class='tooltip' style='color:"+color+"; background-color:red;' id='"+state.history[0].variables["currentRegion"]+"'>"+char +"</span>";
}

//Check if the map character is ADJACENT to the current region's character.
//Only allowed to travel to adjacent squares on map.

else if(getCanTravelHere(vx, vy, loc_x, loc_y)){

//((vx === loc_x-1) || (vx === loc_x+1) || (vx === loc_x)) && ((vy === loc_y-1) || (vy === loc_y+1) || (vy === loc_y))

if(destination != "u"){
//old one where you actually enter the destination is [["+char+"|"+destination+"]
//testing [["+char+"|Map]
return "<span class='tooltip-travellable' style='color:"+color+";' id='"+destination+"'>"+"[["+char+"|Map][$currentRegion = '"+destination+"'; $location_x = "+vx+"; $location_y ="+vy+";]]"+"</span>";
}
else{
return "<span class='tooltip-travellable' style='color:"+color+";' id='???'>"+"[["+char+"|???][$currentRegion = '???'; $location_x = "+vx+"; $location_y ="+vy+";]]"+"</span>";
}

}

else{

//Not adjacent. Just print the char. No linko!

	if(destination != "u"){
	return "<span class='tooltip' style='color:"+color+"' id='"+destination+"'>"+char+"</span>";
	}
	else{
	return "<span class='tooltip' style='color:"+color+"' id='???'>"+char+"</span>";
	}
}

	}



	getCanTravelHere = function(vx, vy, loc_x, loc_y){

//North travel?
if((vx === loc_x) && (vy === loc_y-1)){
	return state.history[0].variables["travel"]["n"];
}
//South travel?
else if((vx === loc_x) && (vy === loc_y+1)){
	return state.history[0].variables["travel"]["s"];
}
//East travel?
else if((vx === loc_x+1) && (vy === loc_y)){
	return state.history[0].variables["travel"]["e"];
}
//West travel?
else if((vx === loc_x-1) && (vy === loc_y)){
	return state.history[0].variables["travel"]["w"];
}
//North east?
else if((vx === loc_x+1) && (vy === loc_y-1)){
	return state.history[0].variables["travel"]["ne"];
}
//North west?
else if((vx === loc_x-1) && (vy === loc_y-1)){
	return state.history[0].variables["travel"]["nw"];
