macros['data'] = 
{
	handler: function(place, object, parameters, parser)
	{
	  if(parameters.length > 3){
		var command = parameters[0];
		var name = parameters[1];
		var number = Number(parameters[2]);
		var show = parameters[3];
		var data = state.history[0].variables["player"]["data"];
		var data_tb = state.history[0].variables["current_data_tb"];
		var max_data_tb = state.history[0].variables["max_data_tb"];

		if(command == "give" && data_tb < max_data_tb) 
		{			

			var index = arrayObjectIndexOf(data, name, "Name");
			if (index > -1) {
				//We must check if the new number + the old > max data
				var new_number = Number(data[index].Number)+number;
				//only give up to the maximum
				if(new_number+data_tb > max_data_tb){
					new_number = max_data_tb - data_tb;
					number = new_number;
				}

				data[index].Number = new_number;
				state.history[0].variables["current_data_tb"] = data_tb+new_number;
				data_tb = data_tb+new_number;
			}else{

				if(number+data_tb > max_data_tb){
					number = max_data_tb - data_tb;
				}


				var obj = new Object();
				obj.Name = name;
				obj.Number = number;
				state.history[0].variables["current_data_tb"] = data_tb+number;
				data_tb = data_tb+number;
				data[data.length] = obj;
			}			 

			if(show == 1) new Wikifier(place, "<span style='color:green;'> You have acquired "+number+"TB of "+name+" data.</span>");
			if(data_tb == max_data_tb) new Wikifier(place, "<br><span style='color:red;'>Data capacity reached. Deposit your data at Your Habitat before seeking more.</span>");
		}

		//CASE 2: Data capacity limit reached.
		else if(command == "give" && data_tb == max_data_tb){
			if(show == 1){
				new Wikifier(place, "<span style='color:red;'>Data capacity reached. Deposit your data at Your Habitat before seeking more.</span>");

			}			
			//Just ignore if it's invisible.
		}

		else if(command == "take")
		{

			var index = arrayObjectIndexOf(data, name, "Name");
			if (index > -1) {
				var new_number = Number(data[index].Number)-number;

				//Make sure it's not negative. No such thing as negative data.
				if(new_number < 0){
					data[index].Number = 0;
					number = number+new_number;				
					state.history[0].variables["current_data_tb"] = data_tb-number;				
				}
				else{
					data[index].Number = new_number;
					state.history[0].variables["current_data_tb"] = data_tb-number;
				}
			if(show == 1 && number != 0) new Wikifier(place, "<span style='color:red'> You have lost "+number+"GB of "+name+" data.</span>");


			}

		}

	  }

	}
}


