{
(if: $cable is "shiny" or it is "black")[
	(set: $connect to "connect")
    (set: $con to "to")
	]
(elseif: $cable is "oozing")[
	(set: $connect to "squidge")
    (set: $con to "onto")
    ]
(elseif: $cable is "sticky")[
	(set: $connect to "glue")
    (set: $con to "to")
    ]
