EVERYTHING GOES DARK

[[...->ASSIGNMENT-2-ORBIT-1]]<img class="jsExecutionBlock" src="data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7" 
onload="
    doPassageLoadMaintenance();

    if (!window.epilepsy) {
        makeStoryIntoShakeRotator();
        var container = $('<div class=\u0027transitionContainer\u0027 style=\u0027position:absolute; left: 0; top: 0; width: 100%; height: 100%; opacity: 1; transition: opacity 3s;\u0027></div>');
        var contents = $('tw-passage *');
        contents.remove();
        container.append(contents);
        $('tw-passage').append(container);

        $(container).css({
            '-webkit-transition': 'opacity 3s',
            '-moz-transition': 'opacity 3s',
            '-o-transition': 'opacity 3s',
            'transition': 'opacity 3s',
