			originalText: $(_element).text()
		};
	}
	function convertTextElementsIntoSingleCharSpans(elements) {
		for (var ii = 0; ii < elements.length; ii++) {
			var spans = [];
			var text = $(elements[ii]).text();
			for (var jj = 0; jj < text.length; jj++) {
				var span = document.createElement('span');
				span.innerHTML = text[jj];
				spans.push(span);
			}

			elements[ii].innerHTML = '';
			for (var xx = 0; xx < spans.length; xx++) {
