	var CHANCE_OF_GLITCH = 0.0125;
	function doGlitchActions(textElement) {
		if (textElement.glitchType === 'none' &&
				Math.random() < CHANCE_OF_GLITCH) {
			setGlitchType(textElement);
			doGlitch(textElement);
		} 
		if (textElement.framesRemaining <= 0) {
			reverseGlitch(textElement);
			textElement.glitchType = 'none';
			return;
		}

		textElement.framesRemaining--;
	}

	var GLITCH_TYPES = [
