		'mirrorVertical',
		'mirrorHorizontalAndVertical',
		'replaceWithUnicode',
		'replaceWithSpace'];
	var MAX_GLITCH_FRAMES = 5;
	function setGlitchType(textElement) {
		textElement.glitchType = GLITCH_TYPES[Math.floor(Math.random() * GLITCH_TYPES.length)];
		textElement.framesRemaining = Math.floor(Math.random() * MAX_GLITCH_FRAMES);
	}

	var LETTERS = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
	function doGlitch(textElement) {
