@import url(http://fonts.googleapis.com/css?family=Playfair+Display);

body {
  margin: 7%;
  background-color:#fff;
  margin: 0;
  text-align:center;
}

#passages{
  margin: 0;
  padding: 0;
  border: 0;
  width:80%;
  margin: auto;
}
.passage:last-child {
font-family: 'Playfair Display', serif;
	font-size:3em; 
	color: gray;
	text-shadow: #888 0 0 0.05em;
}

.passage .title {display:
none}

.passage:not(:last-child) {
font-family: 'Playfair Display', serif;
	font-size:2em; 
	color: gray;
	text-shadow: #888 0 0 0.05em;
}

@media screen and (max-width: 960px) {
  .passage {
    font-size: 4em;
  }
}
@media screen and (max-width: 640px) {
  .passage {
    font-size: 3em;
  }
}
a.internalLink, a.externalLink {
  color: NavyBlue;
  text-shadow: #eee 0 0 0.1em;
}
a.internalLink:hover, a.externalLink:hover {
  color: Blue;
  text-decoration: none;
  text-shadow: #fff 0 0 0.09em;
}
#sidebar {
	display:none;
}

#footer, #floater { display: none }

.three-col {
       -moz-column-count: 2;
       -moz-column-gap: 20px;
       -webkit-column-count: 2;
       -webkit-column-gap: 20px;
}

.revision-span-in {
	opacity: 0;
}
.revision-span:not(.revision-span-out) {
	transition: 1s; -webkit-transition: 1s;
}
.revision-span-out {
	position:absolute;
	opacity: 0;
}

