passages.text:
@import 'https://fonts.googleapis.com/css?family=Comfortaa:400,700|Work-Sans:300|Scope+One';

/*! <<replacelink>> macro set for SugarCube 2.x */
.revision-span-in {
	opacity: 0;
}
.revision-span:not(.revision-span-out) {
	-webkit-transition: 1s;
	transition: 1s;
}
.revision-span-out {
	position: absolute;
	opacity: 0;
}

html{

background: rgb(0,17,30);
}

.passage {
	/* This only affects passages */
	width: 70%;
	margin:auto;
color: rgb(198, 198, 198);
font-family: 'Scope One', serif;
font-weight:300;
margin-top:10%;
font-size:1.6em;
line-height:1.3;
cursor:default;
margin-bottom:10%;
}

a{
  background-image: radial-gradient(ellipse farthest-corner,rgba(0,0,0,0),rgba(0,0,0,0));
  text-align: left;
  color: rgb(222, 222, 222);
text-shadow: 
    0 0 20px rgba(34,252,255,1),
    0 0 40px rgba(34,141,255,.8),
    0 0 60px rgba(34,141,255,0.8),
    0 0 80px rgba(37,34,255,0.5); 

font-style:italic;
text-decoration:none;

}

a:hover {
text-shadow: 
    0 0 20px rgba(34,252,255,.6),
    0 0 40px rgba(34,141,255,0.5),
    0 0 60px rgba(34,141,255,0.3),
    0 0 80px rgba(37,34,255,0.1);
font-style:normal;
}

.start .passage {
font-size:initial;
margin-top: initial;
margin-top:5%;
width:initial;
width:60%;
line-height:1;
}

h2 {

text-align: right;
font-family: 'Comfortaa', sans-serif; 
  font-weight: 100;
  background-image: radial-gradient(ellipse farthest-corner,rgba(0,0,0,0),rgba(0,0,0,0));
  color: #fee;
  font-size: 2.3em;
text-shadow:none; 
  text-shadow: 
    0 0 20px rgba(255, 0, 0,0.9),
    0 0 40px rgba(172, 0, 7,0.6),
    0 0 60px rgba(199, 0, 8,0.3),
    0 0 80px rgba(254, 13, 22,0.1); 
}

h2 a {
text-shadow:none; 
  text-shadow: 
    0 0 20px rgba(255, 0, 0,0.9),
    0 0 40px rgba(172, 0, 7,0.6),
    0 0 60px rgba(199, 0, 8,0.3),
    0 0 80px rgba(254, 13, 22,0.1); 
font-style:no;

}
h2 a:hover {
text-shadow:none;
text-shadow: 
    0 0 20px rgba(255, 100, 100,1),
    0 0 40px rgba(255, 63, 63,0.8),
    0 0 60px rgba(199, 0, 8,0.5),
    0 0 80px rgba(254, 13, 22,0.1); 

}

h3 {
text-align: right;
font-size: 1.5em;
font-weight: 300;
}

h3 a{
text-shadow:initial;
  background-image: radial-gradient(ellipse farthest-corner,rgba(0,0,0,0),rgba(0,0,0,0));
  text-align: left;
  color: #fee;
  text-shadow: 
    0 0 20px rgba(34,252,255,.6),
    0 0 40px rgba(34,141,255,0.5),
    0 0 60px rgba(34,141,255,0.5),
    0 0 80px rgba(37,34,255,0.1); 
font-weight:bold;

}

h3 a:hover {
text-shadow:initial;
text-shadow: 
    0 0 20px rgba(34,252,255,1),
    0 0 40px rgba(34,141,255,.8),
    0 0 60px rgba(34,141,255,0.8),
    0 0 80px rgba(37,34,255,0.2); 
}


h4 {
font-family: 'Comfortaa', sans-serif; 
  font-weight: 100;
  background-image: radial-gradient(ellipse farthest-corner,rgba(0,0,0,0),rgba(0,0,0,0));
  color: #fee;
  text-transform: uppercase;
  font-size: 1 em;
  text-shadow: 
    0 0 20px rgba(255, 0, 0,0.9),
    0 0 40px rgba(172, 0, 7,0.6),
    0 0 60px rgba(199, 0, 8,0.3),
    0 0 80px rgba(254, 13, 22,0.1);
position: relative;
right:5%;
}
ul {list-style:none;}
li:before {
  content: "➥ ";
  background-image: radial-gradient(ellipse farthest-corner,rgba(0,0,0,0),rgba(0,0,0,0));
  text-align: left;
  color: rgb(222, 222, 222);
text-shadow: 
    0 0 20px rgba(34,252,255,1),
    0 0 40px rgba(34,141,255,.8),
    0 0 60px rgba(34,141,255,0.8),
    0 0 80px rgba(37,34,255,0.5); 
}

.arrow {
text-align: right;
}
.arrow a{
font-weight:bold;
font-size:2em;
font-style:normal;
}

.arrow a:hover{
font-style: italic;
}

.q li:before {
  content: "? ";
  background-image: radial-gradient(ellipse farthest-corner,rgba(0,0,0,0),rgba(0,0,0,0));
  text-align: left;
dont-weight:bold;
  color: rgb(222, 222, 222);
text-shadow: 
    0 0 20px rgba(34,252,255,1),
    0 0 40px rgba(34,141,255,.8),
    0 0 60px rgba(34,141,255,0.8),
    0 0 80px rgba(37,34,255,0.5); 
}

@keyframes neonspark  { 
0% { text-shadow: none; 
opacity: .5;}
30% { text-shadow: 0 0 30px rgba(34,141,255,0.6); }
60% {
  text-shadow: 0 0 30px rgba(34,141,255,0.6),
      0 0 60px rgba(34,141,255,0.4); 
  }
80% { text-shadow: none; }
100% { 
    text-shadow: 0 0 30px rgba(34,252,255,0.6),
      0 0 60px rgba(34,141,255,0.4),
      0 0 100px rgba(34,141,255,0.2),
      0 0 90px rgba(37,34,255,0.1);
  }
}

h1#neon-tubing {
  text-align:left;
  color: rgba(34,141,255,0.8);
font-family: 'Comfortaa', sans-serif;
}

h1#neon-tubing { 
  background-image: radial-gradient(ellipse farthest-corner,rgba(0,0,0,0),rgba(0,0,0,0));
  text-align: left;
  color: #fee;
  text-transform: uppercase;
  font-size: 5.9em; 
  animation: neonspark 1s 2s forwards;
  text-shadow: 
    0 0 20px rgba(34,252,255,0.6),
    0 0 40px rgba(34,141,255,0.4),
    0 0 60px rgba(34,141,255,0.2),
    0 0 80px rgba(37,34,255,0.1); 
}
#neon-tubing span { animation: neonspark 4s 3s infinite;  }
#neon-tubing .flicker { animation: neonspark .5s linear 10s infinite  }
#neon-tubing .one { animation: neonspark 2s 5s infinite;  }
#neon-tubbing .two { animation: neonspark 8s 2s infinite;  }
