passages.text:
/*
Modified from an original by Glorious Trainwrecks
http://www.glorioustrainwrecks.com/node/5163
*/

@import url(http://fonts.googleapis.com/css?family=Montserrat+Alternates:700);

html {
  /* Vertical colour gradient */
  background-image: linear-gradient(to bottom,  black, blue);
  background-image: -webkit-linear-gradient(top,  black, blue);
  background-attachment: fixed;

  /* Fallback colour */
  background-color: silver;

}
body {
  /* Remove default styles */
  background-color: transparent;

  background-image: [img[back2]];
  background-repeat: no-repeat;
  min-height: 420px;

  margin: 10% 0 0 0;
  font-size: 100%;
  /* Used to center the box */
  text-align: center;
  font-family: 'Montserrat Alternates', sans-serif;
}

blockquote {
  background-color: white;
  font-family: sans-serif;
  padding: 15px;
}

#passages {
  /* Box background (white with 70% opacity) */
  background-color: rgba(255, 255, 255, 0.7);

  /* Border */
  border: 2px solid white;

  /* Rounded corners */
  border-radius: 1em;

  /* Box width */
  width: 60%;

  /* Center the box */
  display: inline-block;
  min-height: 40%;
  margin:auto;
  margin-bottom: 5%;
  padding: 0px;
}

.passage {
  margin: 0px;
  /* Inner margin within the box */
  padding: 2em;

  /* Text formatting */
  color: black;
  font-size: 100%;
  text-align:justify;
}

p + p {
  margin-top: 0.5em; 
} 

.ooc {
  padding-left: 3em;
  font-style: italic;
  color: DarkSlateGray;
}

/* No sidebar */
#sidebar {
  display:none;
}

/* Links */
a.internalLink, a.externalLink {
  color: #000060;
}
a.internalLink:hover, a.externalLink:hover {
  color: RoyalBlue;
  text-decoration: none;
}

/* Shrink the page when viewed on devices with a low screen width */
@media screen and (max-width: 960px) {
  .passage { font-size: 90%;}
  #passages { width: 70%; }
}
@media screen and (max-width: 840px) {
  .passage {  font-size: 87.5%; }
  #passages { width: 80%; }
}
@media screen and (max-width: 720px) {
  .passage { font-size: 75%; }
  #passages { width: 90%; }
}
#ui-bar {
  display: none;
}
