passages.text:
Config.saves.autosave = true;

Config.saves.isAllowed = function () {
	return !tags().includes("front-matter");
};

l10nStrings.restartPrompt = "Return to the title screen? (Your progress has been saved automatically.)"

Config.debug = false;

window.constants = {
	small_change: 3,
	medium_change: 5,
	big_change: 10
}

$(document).on(':passagedisplay', function (ev) {
	setPageElement("sidebar", "Sidebar");
});

window.goBack = function() {
	Engine.backward();	
}

window.goForward = function() {
	Engine.forward();	
}

window.restart = function() {
	UI.restart();
}

body {
	background-color: #000;
}

/* Game passages */
#passages {
	position: absolute;
	top: 0%;
	left: 50%;
	transform: translate(-50%, 0%);
	box-sizing: border-box;
	width: 40em;
	max-width: 100%;
	padding: 1em 1em 2em;	/* Top, sides, bottom */
	margin: 3em 0em 0em;	/* Top, sides, bottom */
	color: #FFF;
	/*font-family: monospace;*/
}


a {
	box-sizing: content-box;
	color: #6ad;
	border: 1px solid transparent;
}

a:hover {
	color: #8af;
	border: 1px solid #8af;
	text-decoration: none;
}


/* Title screen */
.title {
	font-size: 300%;
	font-family: monospace;
}

.subtitle {
	font-size: 150%;
	font-family: monospace;
}



.disabled-button {
	color: #666666
}



/* Stats box */
#sidebar {
	position: fixed;
	top: 0;
	left: 0;
}

#sidebar a {
	color: #666666;
	box-sizing: initial;
	border: initial;
	margin: 0.5em;
	font-size: 2em;
}

#sidebar a:hover {
	color: #FFFFFF;
}

div.sidebar-section {
	display: block;
}




.front-matter #passages {
	background-color: transparent;
	border: 0px;
	box-shadow: none;
	color: #FFFFFF;
	margin: 3em 0em 0em;	/* Top, sides, bottom */

}


.center {
	text-align: center;
}



/* Small screen/mobile formatting */
@media screen and (max-width: 52em) {
	#sidebar {
		top: 0;
		left: 0;
		width: 100%;
		height: 3em;

	}

	#sidebar {
			/* Fade from black to transparent */
		background-color: #000000;	/* For browsers that don't support gradients */
		background: -webkit-linear-gradient(rgba(0,0,0,1) 66%,rgba(0,0,0,0)); /*Safari 5.1-6*/
		background: -o-linear-gradient(rgba(0,0,0,1) 66%,rgba(0,0,0,0)); /*Opera 11.1-12*/
		background: -moz-linear-gradient(rgba(0,0,0,1) 66%,rgba(0,0,0,0)); /*Fx 3.6-15*/
		background: linear-gradient(rgba(0,0,0,1) 66%, rgba(0,0,0,0)); /*Standard*/
	}

	div.sidebar-section {
		display: contents;
	}


}


/* Debug box (will not appear correctly on mobile) */
#debug-box {
	font-family: monospace;
	color: #333333;
	font-size: 75%;
	position: fixed;
	right: 10px;
	bottom: 10px;
}

#debug-box a {
	background: transparent;
	color: #333333;
	border-color: #333333;
}

input[type="text"] {
	color: #000000;
}




.title-screen-version-number {
	text-align:center;
	position: fixed;
	bottom: 5px;
	right: 5px;
	font-size: 60%;
	opacity: 0.15;
}




.loc {
	font-weight: bold;
}


.graffiti {
	font-weight: bold;
	font-size: 200%;
	font-family: "Comic Sans MS", cursive, sans-serif;
}


.debug-button {
	font-size: 0.5em;
	font-family: monospace;

}
