function findGetParameter(parameterName) {
    var result = null,
        tmp = [];
    var items = location.search.substr(1).split("&");
    for (var index = 0; index < items.length; index++) {
        tmp = items[index].split("=");
        if (tmp[0] === parameterName) result = decodeURIComponent(tmp[1]);
    }
    return result;
}

document.addEventListener('click', function (event) {

	if (event.target.matches('tw-link')) {
		var passageName = event.target.getAttribute('passage-name');
		if (findGetParameter('rabbit') == 'chris')
			gtag('event', 'Navigation', {
		  		'event_label': passageName,
		  		'event_category': 'ChrisRabbitClick',
			});
		if (findGetParameter('rabbit') == 'daniel') {
			gtag('event', 'Navigation', {
		  		'event_label': passageName,
		  		'event_category': 'DanielRabbitClick',
			});		
		} else {
			gtag('event', 'Navigation', {
		  		'event_label': passageName,
		  		'event_category': 'GuestClick',
			});			
		}
	}

}, false);  
</script>
