config.ui.stowBarInitially = true; // the UI bar will start stowed (shut)
config.disableHistoryTracking = true;
config.disableHistoryControls = true;
$("#menu-saves").remove();

/***********************************************************************
	BLEACHED - A largely white style for SugarCube (v2.0.0+)
***********************************************************************/
body {
	background: url('dune5.jpg') no-repeat bottom center fixed; 
	background-size: cover;
    background-color: #e8eec9;
	color: 	#111;
	font-family: "Times New Roman", Times, serif;
	font-size: 1.5em;
}
a {
	color: blue;
	}
a:hover {
	color: blue;
	text-shadow: 2px 2px 5px blue;
	text-decoration: none;
}
button {
	color: #111;
	background-color: #acf;
	border-color: #8ad;
}
button:hover {
	background: url('images.jpg');
	background-color: #8ad;
	border-color: #68b;
}
button:disabled {
	background-color: #ccc;
	border-color: #aaa;
}
input, select, textarea {
	color: #111;
	border-color: #ccc;
}
input:focus, select:focus, textarea:focus,
input:hover, select:hover, textarea:hover {
	background-color: #eee;
	border-color: #111;
}
hr {
	border-color: #111;
}

.error {
	background-color: #eaa;
	border-left-color: #d77;
}

#ui-bar {
	background: url('images.jpg') repeat top right; 
	background-color: #eee;
	border-color: #ccc;
	color: #111;
}
#ui-bar hr {
	border-color: #ccc;
}
#ui-bar-toggle {
	color: #111;
	border-color: #ccc;
}
#ui-bar-toggle:hover {
	background-color: #ccc;
	border-color: #111;
}
#menu ul {
	border-color: #ccc;
}
#menu li:not(:first-child) {
	border-top-color: #ccc;
}
#menu li a {
	color: #111;
}
#menu li a:hover {
	background: url('images.jpg');
	border-color: #111;
}

/* Default dialog styling */
#ui-overlay {
	background-color: #777;
}
#ui-dialog-titlebar {
	background-color: #ccc;
}
#ui-dialog-close:hover {
	background-color: #b44;
	border-color: #a33;
}
#ui-dialog-body {
	background-color: #fff;
	border-color: #ccc;
}
#ui-dialog-body hr {
	background-color: #ccc;
}

/* List-based dialog styling */
#ui-dialog-body.list li:not(:first-child) {
	border-top-color: #ccc;
}
#ui-dialog-body.list li a {
	color: #111;
}
#ui-dialog-body.list li a:hover {
	background-color: #ccc;
	border-color: #111;
}

/* Saves dialog styling */
#ui-dialog-body.saves > *:not(:first-child),
#ui-dialog-body.saves tr:not(:first-child) {
	border-top-color: #ccc;
}
#ui-dialog-body.saves .empty {
	color: #777;
}

/* Settings dialog styling */
#ui-dialog-body.settings button[id|="setting-control"] {
	color: #111;
	border-color: #ccc;
}
#ui-dialog-body.settings button[id|="setting-control"]:hover {
	background-color: #eee;
	border-color: #111;
}
#ui-dialog-body.settings button[id|="setting-control"].enabled {
	background-color: #9e9;
	border-color: #7c7;
}
#ui-dialog-body.settings button[id|="setting-control"].enabled:hover {
	background-color: #7c7;
	border-color: #5a5;
}
